import React, { useState } from 'react';
import db from '../firebase'
import './C.css';
import Products from './product';


const PAGE_PRODUCTS = 'products';
const PAGE_CART = 'cart'
const senderEmail = "cbakeTeam@gmail.com"
const templateId = "template_j303hv9"
const Email = "franciscor343@gmail.com"
const FirstName = "franciscor343@gmail.com"
const LastName = "franciscor343@gmail.com"


function Cart(){
    const [cart, setCart] = useState([]);
    const [page, setPage] = useState ('products');



    const addToCart = (product) => {
        setCart([...cart, {...product}]);
    };

    const navigateTo = (nextPage) => {
        setPage(nextPage);
    };

    const removeFromCart = (productToRemove) => {
        setCart(cart.filter((product) => product !== productToRemove))
    }

    const handleSumbit = (e) =>{
        sendFeedback({
            templateId,
            senderEmail,
            receiverEmail:Email,
            FirstName,
            LastName,
          })
        e.preventDefault();
        //here we should probably change the status of the order so that the cart empties
        // and all of the information goes from the cart to the order history page
    }
    const sendFeedback = ({
        templateId,
        senderEmail,
        receiverEmail,
        FirstName,
        LastName,
      }) => {
        window.emailjs
          .send(
            "default_service",
            templateId,
            {
              senderEmail,
              receiverEmail,
              FirstName,
              LastName,
            },
          )
          .then(res => {
            if (res.status === 200) {
              //setFormSubmitSuccessful(true)
            }
          })
          // Handle errors here however you like
          .catch(err => console.error("Failed to send feedback. Error: ", err))
      }




    const renderCart = () => (
        <div>
            <button className="cartButton" onClick={() => navigateTo(PAGE_PRODUCTS)}> Back to home </button>
            <h2 className="cart-title">This is your cart</h2>
            <div className="products">
                {cart.map((product, index) => (
                <div className="product" key={index}>
                <img src={product.image}/>
                    <h3>{product.name}</h3>
                    <h4>{product.cost}</h4>
                    <button onClick={() => removeFromCart(product)}> Remove </button>
                </div>
            ))}
            </div>
            <button className="checkoutBtn" onclick={handleSumbit}>Checkout</button>
        </div>
    );

    return(
        <div className="cart">
        <header>
            <button className="cartButton" onClick={() => navigateTo(PAGE_CART)}> Go to Cart ({cart.length})</button>
        </header>
        
        {page === PAGE_PRODUCTS && <Products addToCart={addToCart}/>}
        {page === PAGE_CART && renderCart()}
        </div>
    )
};

export default Cart;
